    @Test
    public void testUpload4mobile() throws Exception{
        String url = "/api/v1/attachment/upload4mobile";
        String content = "这是一次测试移动端文件上传：abcdefg";
        url = url + "?name=test123.txt&base64str="+Base64.encodeBase64String(content.getBytes());
        this.testPost(url);
    }

